INSERT INTO locations (category, name, jp_name, img, description, address, opening_hours, fee, website, hotels_nearby, latitude, longitude) VALUES ('see','Sensoji Temple','浅草寺','https://cdn.pixabay.com/photo/2017/02/02/10/08/sensoji-2032400_1280.jpg','Sensoji is perhaps the most famous and photographed of Tokyo''s temples. It is the oldest Buddhist temple in the capital, and the five-story pagoda, trails of incense and vast eaves will take you back to a Tokyo of time gone by.','2-3-1 Asakusa, Taito-ku','6:00-17:00','Free','http://www.senso-ji.jp/','https://www.booking.com/searchresults.en-us.html?label=gen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ&sid=b897c7a11056ce24bd2bcb93e941467c&sb=1&src=index&src_elem=sb&error_url=https%3A%2F%2Fwww.booking.com%2Findex.html%3Flabel%3Dgen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ%3Bsid%3Db897c7a11056ce24bd2bcb93e941467c%3Bsb_price_type%3Dtotal%26%3B&ss=sensoji&is_ski_area=0&checkin_year=&checkin_month=&checkout_year=&checkout_month=&group_adults=2&group_children=0&no_rooms=1&b_h4u_keep_filters=&from_sf=1&ss_raw=h&dest_id=&dest_type=&search_pageview_id=0cbe5aebab9702d6&search_selected=false','35.714965','139.796647');
INSERT INTO locations (category, name, jp_name, img, description, address, opening_hours, fee, website, hotels_nearby, latitude, longitude) VALUES ('see','Meiji Jingu','明治神宮','https://images.unsplash.com/photo-1492092789752-9f0dad76aeb7?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2250&q=80','The densely forested grounds of Meiji Jingu occupy a large swath of land bisecting Shinjuku and Shibuya. A walking path cuts through the trees, leading to the shrine in the center. Walking along this misty trail will make you forget you’re in the middle of two of Tokyo’s busiest districts. In addition to being one of the best walks in the city, Meiji Jingu is also a holy place for adherents of Japan''s Shinto religion. During the New Year''s holiday, some three million people pay visit to the central offering hall for the Hatsumode (first shrine visit) custom. On weekends, you may also catch sight of a marriage procession, characterized by a bride wearing an immaculate white hooded kimono, and her groom in traditional black robes.','1-1 Yoyogi Kamizono-cho Shibuya-ku Tokyo','5:00-19:00','¥500','http://www.meijijingu.or.jp/english/','https://www.booking.com/searchresults.html?label=gen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ&sid=b897c7a11056ce24bd2bcb93e941467c&sb=1&src=searchresults&src_elem=sb&error_url=https%3A%2F%2Fwww.booking.com%2Fsearchresults.html%3Flabel%3Dgen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ%3Bsid%3Db897c7a11056ce24bd2bcb93e941467c%3Btmpl%3Dsearchresults%3Bac_click_type%3Db%3Bac_position%3D0%3Bclass_interval%3D1%3Bdest_id%3D900051249%3Bdest_type%3Dlandmark%3Bdtdisc%3D0%3Bfrom_sf%3D1%3Bgroup_adults%3D2%3Bgroup_children%3D0%3Binac%3D0%3Bindex_postcard%3D0%3Blabel_click%3Dundef%3Bno_rooms%3D1%3Boffset%3D0%3Bpostcard%3D0%3Braw_dest_type%3Dlandmark%3Broom1%3DA%252CA%3Bsb_price_type%3Dtotal%3Bsearch_selected%3D1%3Bshw_aparth%3D1%3Bslp_r_match%3D0%3Bsrc%3Dsearchresults%3Bsrc_