INSERT INTO locations (category, name, jp_name, img, description, address, opening_hours, fee, website, hotels_nearby, latitude, longitude) VALUES ('see','Ryogoku Kokugikan','両国国技館','https://images.unsplash.com/photo-1540216268181-08610bb46976?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=934&q=80','The center of sumo in Japan, the Ryogoku Kokugikan arena welcomes world-class wrestlers three times a year for the Tokyo tournaments held in January, May, and September.','1-3-28 Yokoami Sumida-ku Tokyo','10:00-22:00','¥10,000-50,000','http://www.sumo.or.jp/En/','https://www.booking.com/searchresults.html?label=gen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ&sid=b897c7a11056ce24bd2bcb93e941467c&sb=1&src=searchresults&src_elem=sb&error_url=https%3A%2F%2Fwww.booking.com%2Fsearchresults.html%3Flabel%3Dgen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ%3Bsid%3Db897c7a11056ce24bd2bcb93e941467c%3Btmpl%3Dsearchresults%3Bac_click_type%3Db%3Bac_position%3D1%3Bclass_interval%3D1%3Bdest_id%3D14328%3Bdest_type%3Dlandmark%3Bdtdisc%3D0%3Bfrom_sf%3D1%3Bgroup_adults%3D2%3Bgroup_children%3D0%3Binac%3D0%3Bindex_postcard%3D0%3Blabel_click%3Dundef%3Bno_rooms%3D1%3Boffset%3D0%3Bpostcard%3D0%3Braw_dest_type%3Dlandmark%3Broom1%3DA%252CA%3Bsb_price_type%3Dtotal%3Bsearch_selected%3D1%3Bshw_aparth%3D1%3Bslp_r_match%3D0%3Bsrc%3Dsearchresults%3Bsrc_elem%3Dsb%3Bsrpvid%3Deca960e66766014f%3Bss%3DMeiji%2520Jingu%2520Shrine%252C%2520Tokyo%252C%2520Tokyo%2520Prefecture%252C%2520Japan%3Bss_all%3D0%3Bss_raw%3Dmeiji%2520jingu%3Bssb%3Dempty%3Bsshis%3D0%3Bssne%3DHachiko%2520Statue%3Bssne_untouched%3DHachiko%2520Statue%26%3B&ss=Ryogoku+Kokugikan+National+Sumo+Stadium%2C+Tokyo%2C+Tokyo+Prefecture%2C+Japan&is_ski_area=&ssne=Meiji+Jingu+Shrine&ssne_untouched=Meiji+Jingu+Shrine&checkin_year=&checkin_month=&checkout_year=&checkout_month=&group_adults=2&group_children=0&no_rooms=1&from_sf=1&search_pageview_id=eca960e66766014f&ac_sugge