INSERT INTO locations (category, name, jp_name, img, description, address, opening_hours, fee, website, hotels_nearby, latitude, longitude) VALUES ('see','Kabukiza Theatre','歌舞伎座','https://cdn.pixabay.com/photo/2013/02/15/05/35/kabuki-theater-81808_1280.jpg','Even if you have been enchanted by the musicals of Broadway or the West End, you will still find the Kabukiza Theatre experience quite out of this world. The theater, which is directly connected to the Higashi-ginza Station, stages regular performances of the classical Japanese dance-drama artform, Kabuki.','4-12-5 Ginza,Chuo-ku','9:00-23:00','¥20,000-80,000','https://www.kabukiweb.net/theatres/kabukiza/information/index.html','https://www.booking.com/searchresults.html?label=gen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ&sid=b897c7a11056ce24bd2bcb93e941467c&sb=1&src=searchresults&src_elem=sb&error_url=https%3A%2F%2Fwww.booking.com%2Fsearchresults.html%3Flabel%3Dgen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ%3Bsid%3Db897c7a11056ce24bd2bcb93e941467c%3Btmpl%3Dsearchresults%3Bac_click_type%3Db%3Bac_position%3D0%3Bclass_interval%3D1%3Bdest_id%3D257167%3Bdest_type%3Dlandmark%3Bdtdisc%3D0%3Bfrom_sf%3D1%3Bgroup_adults%3D2%3Bgroup_children%3D0%3Binac%3D0%3Bindex_postcard%3D0%3Blabel_click%3Dundef%3Bno_rooms%3D1%3Boffset%3D0%3Bpostcard%3D0%3Braw_dest_type%3Dlandmark%3Broom1%3DA%252CA%3Bsb_price_type%3Dtotal%3Bsearch_selected%3D1%3Bshw_aparth%3D1%3Bslp_r_match%3D0%3Bsrc%3Dsearchresults%3Bsrc_elem%3Dsb%3Bsrpvid%3D120163309da7002d%3Bss%3DRyogoku%2520Kokugikan%2520National%2520Sumo%2520Stadium%252C%2520Tokyo%252C%2520Tokyo%2520Prefecture%252C%2520Japan%3Bss_all%3D0%3Bss_raw%3Dryogoku%2520kok%3Bssb%3Dempty%3Bsshis%3D0%3Bssne%3DMeiji%2520Jingu%2520Shrine%3Bssne_untouched%3DMeiji%2520Jingu%2520Shrine%26%3B&ss=Kabukiza+Theatre%2C+Tokyo%2C+Tokyo+Prefecture%2C+Japan&is_ski_area=&ssne=Ryogoku+Kokugikan+National+Sumo+Stadium&ssne_untouched=Ryogoku+Kokugikan+Natio