INSERT INTO locations (category, name, jp_name, img, description, address, opening_hours, fee, website, hotels_nearby, latitude, longitude) VALUES ('eat','Tonkotsu ramen','豚骨ラーメン','https://cdn.vox-cdn.com/thumbor/57ryI8iyNZ6Su4O52XXsvQa3rkA=/1400x1050/filters:format(jpeg)/cdn.vox-cdn.com/uploads/chorus_asset/file/10462359/Ichiran_Midtown_West_36.jpg','Tonkotsu ramen is a ramen dish that originated in Fukuoka[citation needed], Fukuoka Prefecture on the Kyushu island of Japan, and it is a specialty dish in both Fukuoka and Kyushu. The soup broth is based on pork bones and other ingredients, which are typically boiled for several hours, and the dish is traditionally topped with sliced pork belly and served with ramen noodles that are hard in the center. In Fukuoka, Japan, tonkotsu ramen is referred to as Hakata ramen.','1-22-7 Jinnan Iwamoto Bldg. B1F, Shibuya 150-0041 Tokyo Prefecture','12:00-24:00','¥900-1,500','https://en.ichiran.com/index.php?utm_source=tripadvisor&utm_medium=referral','https://www.booking.com/searchresults.html?label=gen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ&sid=b897c7a11056ce24bd2bcb93e941467c&sb=1&src=searchresults&src_elem=sb&error_url=https%3A%2F%2Fwww.booking.com%2Fsearchresults.html%3Flabel%3Dgen173nr-1FCAEoggI46AdIM1gEaMkBiAEBmAExuAEHyAEM2AEB6AEB-AECiAIBqAIDuALWsJrnBcACAQ%3Bsid%3Db897c7a11056ce24bd2bcb93e941467c%3Btmpl%3Dsearchresults%3Bac_click_type%3Db%3Bac_position%3D0%3Bclass_interval%3D1%3Bdest_id%3D2549%3Bdest_type%3Ddistrict%3Bdtdisc%3D0%3Bfrom_sf%3D1%3Bgroup_adults%3D2%3Bgroup_children%3D0%3Binac%3D0%3Bindex_postcard%3D0%3Blabel_click%3Dundef%3Bno_rooms%3D1%3Boffset%3D0%3Bpostcard%3D0%3Braw_dest_type%3Ddistrict%3Broom1%3DA%252CA%3Bsb_price_type%3Dtotal%3Bsearch_selected%3D1%3Bshw_aparth%3D1%3Bslp_r_match%3D0%3Bsrc%3Dsearchresults%3Bsrc_elem%3Dsb%3Bsrpvid%3D0d9f69f7d11e00be%3Bss%3DHarajuku%252C%2520Tokyo%252C%2520Tokyo%2520Prefecture%252C%2520Japan%3Bss_all%3D0%3Bss_raw%3Dharajuku%3Bssb%3Dempty